import * as React from 'react';
import { getBasicGridData } from '../services';
export const useBasicDemoData = (nbRows, nbCols) => {
  const [data, setData] = React.useState({
    rows: [],
    columns: []
  });
  React.useEffect(() => {
    const newData = getBasicGridData(nbRows, nbCols);
    setData(newData);
  }, [nbRows, nbCols]);
  return data;
};